import streamlit as st
import numpy as np
import pandas as pd
import matplotlib.pyplot as plt
import seaborn as sns
from sklearn.model_selection import train_test_split
from sklearn.naive_bayes import GaussianNB,MultinomialNB
from sklearn.metrics import accuracy_score,precision_score,classification_report,f1_score,recall_score,confusion_matrix,ConfusionMatrixDisplay
from sklearn.feature_extraction.text import CountVectorizer
import pickle
import warnings
warnings.filterwarnings('ignore')
result = None

st.title("Emotion Detection using ML")
text = st.text_input("Enter the text")

import os
# Get the directory of the current script
current_dir = os.path.dirname(__file__)

# Construct the path to the pickle file relative to the current script
pickle_file_path = os.path.join(current_dir, "estimator.pkl")

# Load the pickle file
with open(pickle_file_path, "rb") as f:
    model = pickle.load(f)
if st.button("Submit")==True:
    result = model.predict([text])[0]

if result == "Anger":
    st.image("data:image/jpeg;base64,")
elif result == "Love":
    st.image("data:image/jpeg;base64,")
elif result == 'Sad':
    st.image("data:image/jpeg;base64,")
elif result == "Joy":
    st.image("data:image/jpeg;base64,")
